using System;


namespace NvrOrchestrator.Encryption.SRP
{
    //HomeKit Specific. If you use this in other applications please note that N and g will be different, and some SRP implementations generate x differently.
    public static class Params
    {
        public static byte[] ToBytes(this string hex)
        {
            var hexAsBytes = new byte[hex.Length / 2];

            for (var i = 0; i < hex.Length; i += 2)
            {
                hexAsBytes[i / 2] = Convert.ToByte(hex.Substring(i, 2), 16);
            }

            return hexAsBytes;
        }

        public const int g = 5; // g - generator, modulo N (defined in RFC 5054)
        public static readonly byte[] N =//N - a large, safe prime ( defined in RFC5054)
            ""
                .ToBytes();

    }
}
